CREATE TABLE APPLICATION_LOG (ID NUMBER NOT NULL PRIMARY KEY, SESSION_ID NUMBER, TENANT_ID NUMBER, HOSTNAME VARCHAR2(64), USERNAME VARCHAR2(64), LOGIN_TIME TIMESTAMP, EVENT_TIME TIMESTAMP, EVENT_TYPE VARCHAR2(64), MODEL_CLASS VARCHAR2(64), MODEL_UUID VARCHAR2(36), PARENT_UUID VARCHAR2(36), VALUE_DESC VARCHAR2(255), PREV_VALUE CLOB, NEW_VALUE CLOB);
CREATE SEQUENCE APPLICATION_LOG_SEQ START WITH 1 INCREMENT BY 1;
