CREATE TABLE PERMISSION_DB_BEAN (ID INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY, ACCESS_TYPE VARCHAR(255), CLASS_TYPE VARCHAR(255)) CHARACTER SET=UTF8 COLLATE=UTF8_BIN ENGINE=INNODB;

CREATE TABLE ROLE_DB_BEAN (ID INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY, ROLE_NAME VARCHAR(255), TENANT_ID INTEGER NOT NULL) AUTO_INCREMENT=3 CHARACTER SET=UTF8 COLLATE=UTF8_BIN ENGINE=INNODB;
CREATE INDEX I_ROLE_NAME ON ROLE_DB_BEAN (ROLE_NAME, TENANT_ID);

CREATE TABLE ROLE_PERMISSION_MAP (ROLE_ID INTEGER NOT NULL, PERMISSION_ID INTEGER NOT NULL) ENGINE=INNODB;
CREATE INDEX I_ROLE_PERMISSION ON ROLE_PERMISSION_MAP(ROLE_ID, PERMISSION_ID);

CREATE TABLE USER_DB_BEAN (ID INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY, TENANT_ID INTEGER NOT NULL, PASSWORD BLOB, USER_NAME VARCHAR(255), PASSWORD_HIST1 BLOB, PASSWORD_HIST2 BLOB, PASSWORD_HIST3 BLOB, PASSWORD_HIST4 BLOB, PASSWORD_HIST5 BLOB, PASSWORD_HIST6 BLOB, PASSWORD_HIST7 BLOB, FORCE_RESET INTEGER, PASSWORD_SET_DATE DATE, LOGIN_ATTEMPTS INTEGER, UNLOCK_DATE DATETIME) AUTO_INCREMENT=2 CHARACTER SET=UTF8 COLLATE=UTF8_BIN ENGINE=INNODB;
CREATE INDEX I_USER_NAME ON USER_DB_BEAN (USER_NAME);

CREATE TABLE USER_ROLE_MAP (USER_ID INTEGER NOT NULL, ROLE_ID INTEGER NOT NULL) ENGINE=INNODB;
CREATE INDEX I_USER_ROLE ON USER_ROLE_MAP (USER_ID, ROLE_ID);

CREATE TABLE OBJECT_LOCK_BEAN (ID INTEGER NOT NULL, TENANT_ID INTEGER NOT NULL, CLASS_NAME VARCHAR(255) NOT NULL, LOCK_TIME TIMESTAMP, USER_NAME VARCHAR(255), APPLICATION_NAME VARCHAR(255), HOST_NAME VARCHAR(255), PRIMARY KEY(ID, CLASS_NAME)) CHARACTER SET=UTF8 COLLATE=UTF8_BIN ENGINE=INNODB;

CREATE TABLE SYSTEM_PROPERTY (PROPERTY_NAME VARCHAR(255) NOT NULL, PROPERTY_VALUE VARCHAR(255), TENANT_ID INTEGER NOT NULL, PRIMARY KEY(PROPERTY_NAME, TENANT_ID)) CHARACTER SET=UTF8 COLLATE=UTF8_BIN ENGINE=INNODB;

CREATE TABLE CONFIGURED_ITEM (IDX INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY, TENANT_ID INTEGER NOT NULL, UUID_MSB BIGINT NOT NULL, UUID_LSB BIGINT NOT NULL, ITEM_NAME VARCHAR(255), TYPE_NAME VARCHAR(255), XML LONGTEXT CHARACTER SET UTF8) CHARACTER SET=UTF8 COLLATE=UTF8_BIN ENGINE=INNODB;
CREATE INDEX I_CONFIGURED_ITEM_UUID ON CONFIGURED_ITEM (UUID_LSB, UUID_MSB, TENANT_ID);
CREATE INDEX I_CONFIGURED_ITEM_NAME ON CONFIGURED_ITEM (ITEM_NAME, TENANT_ID);
CREATE INDEX I_CONFIGURED_ITEM_TYPE ON CONFIGURED_ITEM (TYPE_NAME, TENANT_ID);

CREATE TABLE RESOURCE_ROLE_MAP (ROLE_ID INTEGER NOT NULL, RESOURCE_KEY VARCHAR(255) NOT NULL, TENANT_ID INTEGER NOT NULL) CHARACTER SET=UTF8 COLLATE=UTF8_BIN ENGINE=INNODB;
CREATE INDEX I_RESOURCE_ROLE ON RESOURCE_ROLE_MAP (RESOURCE_KEY, TENANT_ID);

CREATE TABLE TENANT (ID INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY, NAME VARCHAR(255) NOT NULL, DESCRIPTION VARCHAR(255), IS_DEFAULT INTEGER DEFAULT 0 NOT NULL CHECK (IS_DEFAULT IN (0, 1))) AUTO_INCREMENT=2 CHARACTER SET=UTF8 COLLATE=UTF8_BIN ENGINE=INNODB;
CREATE INDEX I_TENANT_NAME ON TENANT (NAME);










ALTER TABLE USER_DB_BEAN ADD user_admin INTEGER DEFAULT 0 NOT NULL;

ALTER TABLE USER_DB_BEAN ADD LAST_LOGIN_DATE DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL;

