CREATE TABLE NOBLE_LCH (ID INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY, CREATE_DATE DATETIME NOT NULL, "COUNT" BLOB NOT NULL, "DATE" BLOB NOT NULL, "HOUR" BLOB NOT NULL);

CREATE TABLE NOBLE_L (ID INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY, LCOUNT BLOB NOT NULL, EXPIRY_DATE BLOB NOT NULL, LASTCHECKED BLOB,  WARN_PCNT_USED BLOB NOT NULL, WARN_DAYS_PRIOR_EXPY BLOB NOT NULL, DAYS_WARN_MCU BLOB NOT NULL, USER_COUNT BLOB, USER_DATE BLOB, USER_HOUR BLOB, IS_EXPIRED BLOB, IS_EXCEEDED BLOB, EXCEEDED_DATE BLOB); 

